import React, { useEffect } from 'react';
// import axios from 'axios';

function LandingPage(props) {
    const localVideoref = React.createRef()
    const remoteVideoref = React.createRef()

    // Similar to componentDidMount and componentDidUpdate:
    useEffect(() => { 
        const pc_config = null
        var pc = new RTCPeerConnection(pc_config)

        pc.onicecandidate = (e) => {
            if(e.candidate){
                console.log(JSON.stringify(e.candidate))
            }
        }
        pc.oniceconnectionstatechange = (e) => {
            console.log(e)
        }
    
        pc.onaddstream = (e) => {
            remoteVideoref.current.srcObject = e.stream
        }

        const createOffer = () => {
            console.log('Offer')
            pc.createOffer({offerToReceiveVideo: 1})
            .then(sdp => {
                console.log(JSON.stringify(sdp))
                pc.setLocalDescription(sdp)
            }, e => {})
        }

        const constraints = {video: true}
        const success = (stream) => {
            window.localStream = stream
            localVideoref.current.srcObject = stream
            pc.addStream(stream)
        }
        const failure = (e) => {
            console.log('getUserMedia Errror: ', e)
        }

        navigator.mediaDevices.getUserMedia(constraints)
        .then( success )
        .catch( failure )
    }); 
   



    return (
        <div>
            <video
                style={{
                    width:240, height:240,
                    margin: 5, backgroundColor: 'black'
                }} 
                ref={localVideoref} 
                autoPlay>
            </video>
            <video
                style={{
                    width:240, height:240,
                    margin: 5, backgroundColor: 'black'
                }} 
                ref={remoteVideoref} 
                autoPlay>
            </video>

            <button onClick={createOffer}>Offer</button>
            {/* <button onClick={createAnswer}>Answer</button>
            <br />
            <textarea ref={ref => {textref = ref}} />
            <br />
            <button onClick={setRemoteDescription}>Set Remote Desc</button>
            <button onClick={addCandidate}>Add Candidate</button> */}
        </div>
    );
}

export default LandingPage;
